import React, { useState } from "react"

const CreateNote = (props)=>{
    let [note,Setnote] = useState(
        {
            title:"",
            content:""
        }
    )
const Setcont = (e)=>{
 let {name,value} =e.target;
 Setnote( olddata =>{
     return {...olddata, [name]:value}
 })
}
const Addnote = ()=>{
 props.CreateNote(note);
}
return(
    <>
    <div className="box">
        <form>
            <input
             type="text" 
             placeholder="Title here" 
             autoComplete="off" 
             value ={note.title}
             name="title"
             onChange={Setcont}
             /> 
              <br />
            <textarea
             type="text"
             value ={note.content}
             name="content"
             onChange={Setcont}
              row="" col="" placeholder="create note">
            </textarea>
            <button onClick ={Addnote} id="add"> +</button>
        </form>
    </div>
    </>
)
}
export default CreateNote;
